class Solution {
public:
    int dp[2005][55];
    int n;
    const int modm=1e9+7;
    vector<int> nums;
    int solveDP(int idx,int a){
        if(idx==0){
            if(a<=nums[0]){
                return 1;

            }
            return 0;
        }
        // if((a>nums[idx]))return 0;
        if(dp[idx][a]!=-1)return dp[idx][a];
        int res=0;
        int curb=nums[idx]-a;
        for(int i=0;i<=a;i++){
            if((nums[idx-1]-i)<(curb))break;
            res=(res%modm + (solveDP(idx-1,i)%modm))%modm;
        }
        return dp[idx][a]=res;
    }
    int countOfPairs(vector<int>& num) {
        nums=num;
        n=nums.size();
        for(int i =0;i<(int)nums.size();i++){
            for(int j=0;j<55;j++){
                dp[i][j]=-1;
            }
        }
        int res=0; 
        for(int j=0;j<=nums[n-1];j++){
            res = (res%modm+solveDP(n-1,j))%modm;
            // cout<<j<<" "<<dp[n-1][j]<<"\n"; 
        }
        return res;
    }
};
