/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
  const MOD = 1e9 + 7; 

  const n = nums.length;
  const dp = Array.from({length: n + 1}, () => Array(1001).fill(0)); // Init to constraint max, in trade of performance

  for (let j = 0; j <= nums[0]; j++) {
    dp[0][j] = 1;
  }

  for (let i = 1; i < n; i++) {
    for (let j = 0; j <= nums[i]; j++) {  // arr1[i] = j
      for (let k = 0; k <= j; k++) {  // arr1[i-1] = k <= j (to ensure arr1 is non-decreasing)
        const arr2 = nums[i] - j;  // arr2[i]
        if (arr2 >= 0 && arr2 <= nums[i-1] - k) {  // ensure arr2 is non-increasing
          dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
        }
      }
    }
  }

  let result = 0;
  for (let j = 0; j <= 1000; j++) {
    result = (result + dp[n - 1][j]) % MOD;
  }

  return result;
};
