class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        total = 0
        p = 10 ** 9 + 7
        size = len(nums)

        dp = [[0] * 1001 for _ in range(size)]
        for i in range(0, nums[0] + 1):
            dp[0][i] = 1

        for i, num in enumerate(nums[1:], 1):
            for x in range(0, num + 1):
                y = num - x
                ma = min(nums[i - 1], x)
                mi = nums[i - 1] - y
                for j in range(0, min(mi + 1, ma + 1)):
                    dp[i][x] = (dp[i][x] + dp[i - 1][j]) % p

                # print(i, x, y, 0, mi)

            # for j in range(0, num + 1):
            #     print(i, j, dp[i][j])
#         


        return sum(dp[size - 1]) % p
