class Solution {
    public int countOfPairs(int[] nums) {
        int num0 = nums[0];
        int len = nums.length;
        long[][] inc = new long[len][51];

        int mod = 1_000_000_007;
        for (int i = 0; i <=num0; i++) {
            inc[0][i] = 1;
        }

        for (int i = 1; i < len; i++) {
            int preNum = nums[i - 1];
            int curNum = nums[i];
            for (int j = 0; j <= curNum; j++) {
                for (int pre = 0; pre <= j; pre++) if(preNum - pre >= curNum - j) {
                    inc[i][j] += inc[i - 1][pre];

                    inc[i][j] %= mod;
                }
            }
        }

        long res = 0;
        for (int i = 0; i <= 50; i++) {
            res += inc[len - 1][i];
            res %= mod;
        }
        return (int)res;
    }
}
