class Solution {
public:
    int MOD=1e9+7;
    int dp[2001][52][52];
    int solve(int i,int prev1,int prev2,vector<int>& nums){
        if(i>=nums.size())
            return 1;
        if(dp[i][prev1+1][prev2+1]!=-1)
            return dp[i][prev1+1][prev2+1];
        int ans=0;
        for(int j=0;j<=nums[i];j++){
            if(prev1==-1){
                ans=(ans+solve(i+1,j,nums[i]-j,nums))%MOD;
            }
            else{
                if(j>=prev1 && nums[i]-j<=prev2){
                    ans=(ans+solve(i+1,j,nums[i]-j,nums))%MOD;
                }
            }
        }
        return dp[i][prev1+1][prev2+1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,-1,-1,nums);
    }
};
