class Solution {
public:
    long long rec(vector<int>& nums, int i, int mini, int maxa, vector<vector<vector<long long>>>& dp) {
        if (i == nums.size()) {
            return 1;
        }

        if (dp[i][mini][maxa] != -1) return dp[i][mini][maxa];

        long long sum = 0;
        for (int net = maxa; net <= nums[i]; net++) {
            int first = net;
            int second = nums[i] - net;
            if (first >= maxa && second <= mini) {
                sum += rec(nums, i + 1, second, first, dp);
                sum %= 1000000007;
            }
        }

        return dp[i][mini][maxa] = sum;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51, -1)));
        long long ans = rec(nums, 0, 50, 0, dp);
        return ans;
    }
};
