class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        // DP[i][X][Y] = 
        int n = nums.size();
        vector<vector<long long>> dp(51, vector<long long>(51));
        dp[0][0] = 1;
        accumulate(dp);
        long long ans = 0;
        int p = 1e9 + 7;
        for(int i = 0; i < n; i++) {
            int s = nums[i];
            vector<vector<long long>> next_dp(51, vector<long long>(51));
            long long t = 0;
            for(int x = 0; x <= s; x++) {
                int y = s - x;
                next_dp[x][50 - y] = dp[x][50 - y];
                t += dp[x][50 - y];
            }
            swap(next_dp, dp);
            ans = t % p;
            accumulate(dp);
        }
        return ans;
    }

    void accumulate(vector<vector<long long>> &dp) {
        int p = 1e9 + 7;
        for(int i = 0; i < 51; i++) {
            for(int j = 0; j < 51; j++) {
                long long a = i ? dp[i - 1][j] : 0;
                long long b = j ? dp[i][j - 1] : 0;
                long long c = i && j ? dp[i - 1][ j - 1] : 0;
                dp[i][j] += a + b - c;
                dp[i][j] %= p;
            }
        }
    }
};
