class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int M = 1e9+7;
        int dp[n+1][51];
        for(int i =0;i<=n;i++)
            for(int j= 0;j<=50;j++)
                dp[i][j] = 0;
        dp[0][0] = 1;
        for(int i = 1;i<=nums[0];i++) dp[0][i] = 1;
        int ans = 0;
        for(int i =1;i<n;i++){
            for(int j = 0;j<=nums[i];j++){
                for(int k = 0;k<=j;k++){
                    if(nums[i-1] - k >= nums[i] - j)
                        dp[i][j] = ((dp[i-1][k]%M) + (dp[i][j]%M))%M, dp[i][j] %= M;
                }

            }
        }
        for(int i = 0;i<=50;i++){
            ans = ((dp[n-1][i]%M) + (ans%M))%M;
        }
        return ans;

    }
};
