class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7

        @cache
        def dp(x, y):
            if x == len(nums):
                return 1
            if y > nums[x]:
                return 0
            ans = 0
            for i in range(y, nums[x] + 1):
                if x > 0 and (nums[x - 1] - y) < (nums[x] - i):
                        continue
                ans += dp(x + 1, i)
                ans %= MOD
            return ans
        return dp(0, 0)
