class Solution {
public:
    const int mod=1e9+7;
    long long recur(int i,int mx,int mn,vector<vector<vector<long long>>>&dp,vector<int>&a){
        if (i==a.size()){
            return 1;
        }
        if (i>=0 && mx>=0 && mx<=50 && mn>=0 && mn<=50 && dp[i][mx][mn]!=-1){
            return dp[i][mx][mn];

        }
        long long ans=0;
        for (int j=mx;j<=a[i];j++){
            if ((a[i]-j)<=mn && (a[i]-j)>=0){
                ans+=recur(i+1,j,a[i]-j,dp,a);
                ans%=mod;
            }
        }
        if (i>=0 && mx>=0 && mx<=50 && mn>=0 && mn<=50){
            dp[i][mx][mn]=ans;

        }
        return ans;

    }
    int countOfPairs(vector<int>& a) {
        int n=a.size();
        vector<vector<vector<long long>>>dp(n+1,vector<vector<long long>>(51,vector<long long>(51,-1)));
        long long ans=recur(0,0,100,dp,a);
        return ans;


    }
};
