p = int(1e9 + 7)
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        f = [0] * (51)
        pre = 51
        f[0] = 1
        for cur in nums:
            h = [0] * (cur + 1)
            for x in range(pre + 1):
                y = pre - x
                for a in range(cur + 1):
                    b = cur - a
                    if x <= a and y >= b:
                        h[a] += f[x]
                        h[a] %= p
            pre = cur
            f = h
        return sum(f) % p
