class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int MOD = 1e9 + 7;
        vector<vector<int>> dp(n, vector<int>(51, 0)); 
        for (int v = 0; v <= nums[0]; ++v) {
            dp[0][v] = 1;
        }
        for (int i = 1; i < n; ++i) {
            for (int v = 0; v <= nums[i]; ++v) {
                for (int u = 0; u <= v && u <= nums[i-1]; ++u) {
                    if (  nums[i-1] - u>=nums[i] - v) { 
                        dp[i][v] = (dp[i][v] + dp[i-1][u]) % MOD;
                    }
                }
            }
        }
        int ans = 0;
        for (int v = 0; v <= nums[n-1]; ++v) {
            ans = (ans + dp[n-1][v]) % MOD;
        }

        return ans;
    }
};
