class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        max_val = max(nums) + 1
        dp = [[0] * max_val for _ in range(n + 1)]

        for i in range(nums[0] + 1):
            dp[1][i] = 1

        for i in range(2, n + 1):
            for j in range(nums[i-1] + 1):
                for k in range(j + 1):
                    prev_arr_2 = nums[i-2] - k
                    now = nums[i-1] - j
                    if now <= prev_arr_2:
                        dp[i][j] += dp[i-1][k]
        answer = 0
        for i in range(nums[-1] + 1):
            answer = (answer + dp[n][i]) % MOD


        return answer
