class Solution {
public:
    int dp[2000][51][51];int k=1e9+7;
    int solve(int i,vector<int>&a,int p,int q)
    {
        if(i==a.size())return 1;
        if(i!=0 && dp[i][p][q]!=-1)return dp[i][p][q];
        int ans=0;
        for(int j=0;j<=a[i];j++)
        {
            if(j>=p && a[i]-j<=q)
            ans=(ans+solve(i+1,a,j,a[i]-j))%k;
        }
        if(i==0)return ans;
        return dp[i][p][q]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(0,nums,INT_MIN,INT_MAX);
    }
};
