int MOD = 1e9 + 7;

class FW {
public:
    int n;
    vector<int> root;

    FW(int n) {
        this->n = n;
        root.resize(n + 1, 0);
    }

    void update(int id, int val) {
        while (id <= n) {
            root[id] = (root[id] + val) % MOD;
            id += (id & -id);
        }
    }

    int get(int id) {
        int res = 0;
        while (id > 0) {
            res = (res + root[id]) % MOD;
            id -= (id & -id);
        }
        return res;
    }
};

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 0));
        FW fenwick(51);

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
            fenwick.update(j + 1, dp[0][j]);
        }

        for (int i = 1; i < n; ++i) {
            FW nextFenwick(51);
            for (int j = 0; j <= nums[i]; ++j) {
                int arr2_i = nums[i] - j;
                if (arr2_i <= nums[i - 1]) {
                    dp[i][j] = (dp[i][j] + fenwick.get(min(j, nums[i - 1] - arr2_i) + 1));

                }
                nextFenwick.update(j + 1, dp[i][j]);
            }
            fenwick = nextFenwick;
        }
        int res = 0;
        for (int j = 0; j <= nums.back(); ++j) {
            res = (res + dp[n-1][j]) % MOD;
        }

        return res;
    }
};
