class Solution {
public:
    int countOfPairs(vector<int>& nums) {
      int mod_p = 1e9+7;
      vector<int> dp(51, 0);
      int n = nums.size();
      for (int now = 0; now <= nums[0]; now++) {
        dp[now] = 1;
      }
      for (int i = 1; i < n; i++) {
        vector<int> tmp(dp);
        for (int now = 0; now <= 50; now++) dp[now] = 0;
        for (int last = 0; last <= 50; last++) {
          for (int now = last; now <= nums[i]; now++) {
            if ((nums[i] - now) > (nums[i-1] - last)) continue;
            dp[now] += tmp[last];
            dp[now] = dp[now] % mod_p;
          }
        }
        //for (int now = 0; now <= 50; now++) cout << dp[now] << " ";
        //cout << endl;
      }
      //for (int now = 0; now <= 50; now++) cout << dp[now] << " ";
      //cout << endl;
      int ret = 0;
      for (int now = 0; now <= 50; now++)  {
        ret += dp[now];
        ret = ret % mod_p;
      }
      return ret;

      }
};
