long long mod = pow(10, 9) + 7;

class Solution {
public:
    vector<vector<int>> dp;

    int countOfPairs(vector<int>& nums) {
        long long ans = 0;

        dp = vector<vector<int>>(nums.size(), vector<int>(51));

        for(int idx = 0; idx < nums.size(); idx++) {
            for(int gap = 0; gap <= nums[idx]; gap++) {
                if(idx == 0) {
                    dp[idx][gap] = 1;
                } else {
                    for(int prevGap = 0; prevGap <= gap && prevGap <= nums[idx - 1] && nums[idx - 1] - prevGap >= nums[idx] - gap; prevGap++) {
                        dp[idx][gap] = (dp[idx][gap] + dp[idx - 1][prevGap]) % mod;
                    }
                }

                if(idx + 1 == nums.size()) {
                    ans = (ans + dp[idx][gap]) % mod;
                }
            }
        }

        // for(auto i : dp) {
        //     for(auto j : i) {
        //         cout<<j<<" ";
        //     }
        //     cout<<endl;
        // }

        return ans;
    }
};
