class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [0] * 51
        pre_sum = nums[0]
        for i in range(nums[0]+1):
            dp[i] += 1

        for i in nums[1:]:
            temp = [0] * 51
            for nums1 in range(i+1):
                for j in range(nums1+1):
                    if pre_sum - j >= i - nums1:
                        temp[nums1] = temp[nums1]+ dp[j]

                temp[nums1] = temp[nums1]%(10**9+7)
            pre_sum = i    
            dp = temp[:]
        return sum(dp)%(10**9+7)
