class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [[0] * (max(nums) + 1) for _ in range(len(nums))]
        for i in range(nums[-1] + 1):
            dp[-1][i] = 1
        for i in range(len(nums) - 2, -1, -1):
            m, n = nums[i], nums[i+1]
            for j in range(m + 1):
                if n-(m-j) >= 0:
                    dp[i][j] = sum(dp[i+1][:min(n-(m-j), j) + 1])
        return int(sum(dp[0]) % int(1e9 + 7))
