class Solution {
public:
    int M;
    int countOfPairs(vector<int>& nums) {
        M = 1e9+7;
        int n = nums.size(); 
        vector<vector<int>> dp(n,vector<int>(51,0));
        for(int ai = 0;ai<=nums[n-1];ai++){
            dp[n-1][ai] = 1;
        };
        for(int i =n-2;i>=0;i--){
            for(int ai = 0;ai<=nums[i];ai++){
                int bi = nums[i]-ai;
                int start = max(ai,nums[i+1]-bi);
                int sum = 0;
                for(int s = start;s<=50;s++){
                    sum = (sum%M + dp[i+1][s]%M)%M;
                };              
                //we have to find the number of sequences that are less than 
                dp[i][ai] = sum;
            };
        };
        int ans = 0;
        for(int i = 0;i<=50;i++){
            ans =(ans%M + dp[0][i]%M)%M;
        };
        return ans;

    }
};
