class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int MOD = 1e9+7;
        int n = nums.size();
        vector<vector<int>> dp(n + 1, vector<int>(51, 0));
        for(int i = 0;i<=nums[0];i++)
        {
            dp[0][i] = 1;
        }
        for (int i = 1; i <n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) { 
                for (int k = 0; k <= j; ++k) {  
                    if(nums[i-1] - k >= nums[i]-j)
                    {
                        //cout<<i<<" "<<j<<" "<<k<<" "<<dp[i-1][k]<<endl;
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
        }

        // Sum all dp[n][j] for all j to get the final answer
        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;


    }
};
