class Solution {
public:
    int MOD = 1000000007;

    int countOfPairs2(vector<int> &nums, int i, int p, vector<vector<int>> &cache) {
        int p2 = i == 0 ? nums[i] : nums[i - 1] - p;
        if (i == nums.size() - 1) {
            return max(0, min(nums[i] - p, p2) + 1);
        }
        if (cache[i][p] >= 0) {
            return cache[i][p];
        }
        long long pairs = 0;
        for (int n = max(p, nums[i] - p2); n <= nums[i]; n++) {
            pairs = (pairs + countOfPairs2(nums, i + 1, n, cache)) % MOD;
        }
        cache[i][p] = pairs;
        return pairs;
    }

    int countOfPairs(vector<int> &nums) {
        vector<vector<int>> cache(nums.size(), vector<int>(51, -1));
        return countOfPairs2(nums, 0, 0, cache);
    }
};
