class Solution {
public:
    int count(int curr, int last, vector<int> &nums, vector<vector<int>> &dp){
        if(curr>=nums.size()){
            return 1;
        }
        if(last>nums[curr]){
            return 0 ;
        }
        if(dp[curr][last]!=-1){
            return dp[curr][last] ;
        }
        int last2 = 10000 ;
        if(curr>=1){
            last2 = nums[curr-1] - last ;
        }
        long long int s = 0LL ;
        int M = 1e9 + 7 ;
        for(int i=last;i<=nums[curr];i++){
            if(nums[curr]-i>last2){
                continue ;
            }
            s = s + 1LL*count(curr+1, i, nums, dp) ;
            s = s%M ;
        }
        return dp[curr][last]=s;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size()+1, vector<int>(60, -1)) ;
        return count(0, 0, nums, dp) ;
    }
};
