class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;

        int mod = (int) (1e9 + 7);
        int[][] dp = new int[n][51];
        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                for (int pj = 0; pj <= j; pj++) {
                    int ppj = nums[i - 1] - pj;
                    if (ppj >= 0 && nums[i] - j <= ppj) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][pj]) % mod;
                    }
                }
            }
        }

        int res = 0;
        for (int re : dp[n - 1]) {
            res = (res + re) % mod;
        }
        return res;
    }
}
