class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), m = 51, mod = 1000000007;;
        vector<int> left(n - 1), right(n - 1);
        for (int i = 0; i + 1 < n; ++i) left[i] = max(0, nums[i + 1] - nums[i]);
        for (int i = 0; i + 1 < n; ++i) right[i] = max(0, nums[i] - nums[i + 1]);

        vector<vector<int>> dp(n, vector<int>(m));
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }
        for (int i = 1; i < n; ++i) {
            int d = max(0, nums[i] - nums[i - 1]);
            for (int j = 0; j <= nums[i]; ++j) {
                for (int it = 0; it + d <= j; ++it) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][it]) % mod;
                }
            } 
        }

        int ret = 0;
        for (int j = 0; j < m; ++j) {
            ret = (ret + dp[n - 1][j]) % mod;
        }

        return ret;
    }
};
