class Solution {
public:
    const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        // dp[i][m1][m2] NOW to get nums[0..i] s.t arr1[i-1]<=m1 and arr2[i-1] >=m2

        int n = nums.size();
        int dp[n][51][51];
        memset(dp, 0, sizeof(dp));

        int ans = 0;

        for(int i = 0; i < n; i++){
            for(int x = 0; x<=nums[i]; x++){
                int y = nums[i] - x;
                //this x, y pair has contri at?

                if(i == 0){
                    dp[i][x][y]++;
                }
                else{
                    for(int xP = 0; xP<=min(x, nums[i-1]); xP++){
                        int yP = nums[i-1] - xP;
                        if(yP<y)
                            continue;
                        dp[i][x][y]+=dp[i-1][xP][yP];
                        dp[i][x][y]%=MOD;
                    }
                }

                if(i == n-1){
                    ans+=dp[n-1][x][y];
                    ans%=MOD;
                }
            }
        }

        return ans;

    }
};
