/*
state:
    dp[i][num] = ans from 0...i where ith number is num

transition:
    dp[i][num] = dp[i-1][0] + dp[i-1][1] + dp[i-1][2] + .. + dp[i-1][num] = pf[i-1][num]

answer:
    dp[n-1][0] + dp[n-1][1] + dp[n-1][2] + .. + dp[n-1][50]
base case:
    dp[0][x] ye khudse krlo
*/


class Solution {
    int mod = 1e9+7;

    int mod_add(long long a, long long b){
        a = a%mod;
        b = b%mod;
        return (a+b)%mod;
    }
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<int>> dp(n,vector<int>(51,0));
        for(int num=0;num<=50;num++){
            int x = num, y = a[0]-x;
            if(y>=0){
                dp[0][num] = 1;
            }
        }

        for(int i=1;i<n;i++){
            for(int num=0;num<=a[i];num++){
                // dp[i][num]
                for(int x=0;x<=num;x++){
                    int y = a[i-1]-x;
                    if(y<a[i]-num) break;

                    dp[i][num] = mod_add(dp[i][num], dp[i-1][x]);
                }
            }
        }
        // debug(dp);
        int ans=0;
        for(int num=0;num<=50;num++) ans = mod_add(ans, dp[n-1][num]);
        return ans;
    }
};
