class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        n = len(nums)
        @cache
        def dfs(i,upper,lower):
            if i==-1:
                return 1
            res = 0
            for k in range(nums[i]+1):
                if k<=upper and nums[i]-k>=lower:
                    res+=dfs(i-1,k,nums[i]-k)
            return res % MOD
        return dfs(n-1,inf,-inf) % MOD
