class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int modulo = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n, vector<long long>(51, 0));
        for(int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = 1;
        }
        for(int i = 1; i < n; ++i) {
            for(int j = 0; j <= nums[i]; ++j) {
                for(int k = 0; k <= j; ++k) {
                    if (nums[i]-j <= nums[i-1]-k)
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= modulo;
                }
            }
        }
        long long ans = 0;
        for(int i = 0; i < 51; ++i) {
            ans += dp[n-1][i];
            ans %= modulo;
        }
        return ans;
    }
};
