class Solution {
    int MOD = 1e9 + 7;
public:
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();
        vector<vector<int>> f(n, vector<int>(51));
        for(int i = 0; i <= nums[0]; ++i) f[0][i] = 1;
        for(int i = 1; i < n; ++i) {
            for(int k = 0; k <= nums[i]; ++k) {
                for(int j = 0; j <= k; ++j) {
                    if(nums[i] - k <= nums[i - 1] - j) {
                        f[i][k] = (f[i][k] + f[i - 1][j]) % MOD;
                    }
                }
            }
        }
        int res = 0;
        for(int i = 0; i < 51; ++i) {
            res = (res + f[n - 1][i]) % MOD;
        }
        return res;
    }
};
