class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        mod = 10**9 + 7


        @lru_cache(maxsize=None)
        def count(idx,non_decrease,non_increase):
            if idx == len(nums):
                return 1

            cur_num = nums[idx]

            res = 0

            for lower in range(cur_num+1):
                higher = cur_num - lower

                if lower >= non_decrease and higher <= non_increase:
                    res += count(idx+1,max(lower,non_decrease),min(higher,non_increase))
                    res %= mod
            return res % mod


        return count(0,-inf,inf)
