constexpr long long mod = 1000000007;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long ans = 0;
        vector<vector<int>> dp(nums.size(), vector<int>(60, 0));
        for (int i = 0; i <= nums[0]; ++i) dp[0][i] = 1;

        for (int i = 1; i < nums.size(); ++i)
        {
            int diff = nums[i] - nums[i-1];
            for (int j = 0; j <= nums[i]; ++j)
            {
                if (diff <= 0)
                {
                    for (int k = 0; k <= j; ++k) {
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= mod;
                    }
                }
                else
                {
                    for (int k = 0; k <= j - diff; ++k) {
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= mod;
                    }
                }
            }
        }

        for (int i : dp.back()) ans = (ans + i) % mod;

        return ans;
    }
};
