class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        n = len(nums)
        dp = list()   # up to i when a1[i] = j
        temp = [1] * (nums[0] + 1)
        dp.append(temp.copy())

        for i in range(1, n):
            temp = [0] * (nums[i] + 1)
            for j in range(nums[i] + 1):
                accum = 0
                val = min(j, j + nums[i - 1] - nums[i])
                for k in range(val + 1):
                    accum += dp[i - 1][k]
                temp[j] = accum
            dp.append(temp.copy())

        return sum(dp[-1]) % (pow(10, 9) + 7)
