MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums):
        n = len(nums)
        dp = [[0] * (nums[i] + 1) for i in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            for j in range(nums[i] + 1):
                dp[i][j] = sum(dp[i-1][k] for k in range(j + 1) if nums[i] - j <= nums[i-1] - k) % MOD

        return sum(dp[-1]) % MOD
