class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        max_val = max(nums)

        # Initialize the DP table
        dp = [[[0] * (max_val + 1) for _ in range(max_val + 1)] for _ in range(n)]

        # Base case
        for j in range(nums[0] + 1):
            k = nums[0] - j
            dp[0][j][k] = 1

        # Fill the DP table
        for i in range(1, n):
            for j in range(max_val + 1):
                for k in range(max_val + 1):
                    if dp[i-1][j][k] > 0:
                        for j2 in range(j, nums[i] + 1):
                            k2 = nums[i] - j2
                            if k2 <= k:
                                dp[i][j2][k2] = (dp[i][j2][k2] + dp[i-1][j][k]) % MOD

        # Sum up the results
        result = 0
        for j in range(max_val + 1):
            for k in range(max_val + 1):
                result = (result + dp[n-1][j][k]) % MOD

        return result
