class Solution {
    public int countOfPairs(int[] nums) {
        int[][] dp = new int[nums.length][51];
        for (int i = 0; i<=nums[0];i++){
            dp[0][i]=1;
        }
        int M = 1_000_000_007;
        for (int i = 1; i< nums.length;i++){
            for (int m = 0;m<=nums[i-1];m++){
                for (int n = m; n<=nums[i];n++){
                    if (nums[i-1]-m>=nums[i]-n){
                        dp[i][n]+=dp[i-1][m];
                        dp[i][n]%=M;
                    }
                }
            }
        }
        int answer = 0;
        for (int num: dp[nums.length-1]){
            answer+=num;
            answer%=M;
        }
        return answer;
    }
}
