#include <vector>
#include <cstring>
using namespace std;

#define FOR(i,a,b) for(int i=(a);i<(b);++i)
constexpr int MOD = 1e9 + 7;

class Solution {
public:
    int dp[2000][51][51];

    int dfs(int i, int last1, int last2, const vector<int>& nums) {
        if (i == nums.size()) {
            return 1;  
        }

        if (dp[i][last1][last2] != -1) {
            return dp[i][last1][last2];
        }

        int count = 0;
        int sum = nums[i];

        FOR(arr1, last1, sum + 1) {
            int arr2 = sum - arr1;
            if (arr2 <= last2) {
                count = (count + dfs(i + 1, arr1, arr2, nums)) % MOD;
            }
        }

        return dp[i][last1][last2] = count;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return dfs(0, 0, 50, nums);
    }
};
