#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 0)); // DP table

        // Initialize for the first element
        for (int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = 1; // Only one valid pair (i, nums[0]-i)
        }

        // Fill DP table
        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) { // arr1[i]
                int arr2 = nums[i] - j;
                if (arr2 < 0) continue;
                for (int k = 0; k <= j; ++k) { // arr1[i-1]
                    if (arr2 <= nums[i-1] - k) { // arr2[i-1]
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
        }

        // Sum up the results for the last element
        int result = 0;
        for (int i = 0; i <= nums[n-1]; ++i) {
            result = (result + dp[n-1][i]) % MOD;
        }

        return result;
    }
};
