class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        '''
        split vals of nums into 2 arrays


        '''
        N = len(nums)
        MOD = 10 ** 9 + 7

        @cache
        def calc(index, arr1, arr2):
            if index == N:
                return 1

            total = 0
            for j in range(0, nums[index] + 1):
                left = j
                right = nums[index] - j

                if left >= arr1 and right <= arr2:
                    total += calc(index + 1, left, right)




            return total % MOD


        return calc(0, 0, 51) % MOD
