class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(51, vector<long long>(51, 0));

        for (int i = 0; i <= nums[0]; i++) {
            dp[i][nums[0] - i]++;
        }

        for (int k = 1; k < n; k++) {
            vector<vector<long long>> new_dp(51, vector<long long>(51, 0));
            for (int i = 0; i <= 50; i++) {
                for (int j = 0; j <= 50; j++) {
                    if (dp[i][j] == 0) continue;
                    for (int x = i; x <= nums[k]; x++) {
                        int y = nums[k] - x;
                        if (y <= j) {
                            new_dp[x][y] = (new_dp[x][y] + dp[i][j]) % MOD;
                        }
                    }
                }
            }
            dp = new_dp;
        }

        long long result = 0;
        for (int i = 0; i <= 50; i++) {
            for (int j = 0; j <= 50; j++) {
                result = (result + dp[i][j]) % MOD;
            }
        }

        return result;
    }
};
