class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def recourse(idx, fleurx, fleury):


            if idx == len(nums):
                return 1

            tarjan = nums[idx]

            ans = 0
            for ea in range(fleurx, tarjan + 1):
                inverse = tarjan - ea

                if inverse > fleury:
                    continue
                ans += recourse(idx + 1, ea, inverse)
                ans %= MOD
            return ans


        return recourse(0, 0, 10**12)
