MOD = 10**9 + 7

class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        n = len(nums)
        max_num = 50

        dp = [[0] * (max_num + 1) for _ in range(n)]

        for x in range(nums[0] + 1):
            dp[0][x] = 1

        for i in range(1, n):
            for x in range(nums[i] + 1):
                arr2_i = nums[i] - x
                for prev_x in range(x + 1):
                    if prev_x <= nums[i-1] and arr2_i <= nums[i-1] - prev_x:
                        dp[i][x] = (dp[i][x] + dp[i-1][prev_x]) % MOD

        return sum(dp[n-1][x] for x in range(nums[-1] + 1)) % MOD
