long long mod=1e9+7;
class Solution {
public:
    long long f(int i,int p1,int p2,vector<int>&nums,vector<vector<vector<long long>>>&dp)
    {
        if(i>=nums.size())return 1LL;
        long long ans=0;
        if(dp[i][p1][p2]!=-1)return dp[i][p1][p2]%mod;
        for(int j=0;j<=nums[i];++j)
        {
            int c1=j,c2=nums[i]-j;
            if(c1>=p1 && c2<=p2 && c2>=0)
            {
                ans=(ans+f(i+1,c1,c2,nums,dp))%mod;
            }
        }
        return dp[i][p1][p2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<long long>>>dp(n,vector<vector<long long>>(51,vector<long long>(51,-1)));
        return f(0,0,50,nums,dp)%mod;
    }
};
