class Solution {
#define ll long long
#define mod (long long)(pow(10, 9) + 7)
    ll solve(int i, int prev, int &n, vector<int> &nums, vector<vector<ll>> &dp)
    {
        if(i == n)
            return 1;
        if(dp[i][prev] != -1)
            return dp[i][prev];
        ll curr = 0;
        if(i == 0)
        {
            for(int j = 0; j <= nums[i]; j++)
                curr += solve(i + 1, j, n, nums, dp) % mod, curr %= mod;
            return dp[i][prev] = curr;
        }
        else
        {
            for(int j = prev; j <= nums[i]; j++)
            {
                ll second_prev = nums[i - 1] - prev;
                if(nums[i] - j <= second_prev)
                    curr += solve(i + 1, j, n, nums, dp) % mod, curr %= mod;
            }
            return dp[i][prev] = curr;
        }
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<ll>> dp(n, vector<ll> (51, -1));
        return solve(0, 0, n, nums, dp);
    }
};
