const int MOD = 1e9 + 7;
int add(int a, int b) {
    a += b;
    if (a >= MOD) a -= MOD;
    return a;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int N = 50;

        vector dp(n + 1, vector(N + 1, 0));
        for (int j = 0; j <= nums[0]; j++)
            dp[0][j] = 1;

        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= N; j++) {
                for (int k = j; k <= N; k++) {
                    int bj = nums[i - 1] - j;
                    int bk = nums[i] - k;
                    if (bk < 0) continue;
                    if (bk <= bj) {
                        dp[i][k] = add(dp[i][k], dp[i - 1][j]);
                    }
                }
            }
        }

        int ans = 0;
        for (int j = 0; j <= N; j++) {
            ans = add(ans, dp[n - 1][j]);
        }

        return ans;
    }
};
