class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;
    int n;

    int rec(int i, int p1, int p2, vector<int>& nums){
        // Pruning
        if(i >= n) return 1;

        // Cache check
        if(dp[i][p1][p2] != -1) return dp[i][p1][p2];
        //compute
        int ans = 0;
        for(int j = 0; j <= nums[i]; j++){
            int x = j, y = nums[i] - x;
            if(p1 <= x && p2 >= y){
                ans = (ans + rec(i + 1, x, y, nums)) % MOD;
            }
        }

        // Save and return
        return dp[i][p1][p2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return rec(0, 0, 50, nums);
    }
};
