class Solution {
public:
    int dp[2005][60][60];
    vector<int>a;
    int n;
    const int M = (int)1e9+7;
    int solve(int i, int x , int y) {
        if(i == n) {
            return 1;
        }
        if(dp[i][x][y] != -1) return dp[i][x][y];

        int ans = 0;
        for(int j = x ; j <= 50 ; j++) {
            int Y = a[i] - j;
            if(Y >= 0 && Y <= y && j+Y == a[i]) {
                ans += solve(i+1,j,Y);
                ans %= M;
            }
        }
        return dp[i][x][y] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        a = nums;
        n = (int)a.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,0,50);
    }
};
