class Solution {
public:

    long long dp[2001][51][51];
    long long mod = 1e9+7;
    long long solve(int i,int pre1,int pre2,vector<int>& nums)
    {
        if(i==nums.size())
            return 1;

        if(dp[i][pre1][pre2]!=-1)
            return dp[i][pre1][pre2]%mod;

        long long ans = 0;
        for(int x=0;x<=nums[i];x++)
        {
            int y = nums[i]-x;
            if(pre1<=x && pre2>=y)
            {
                ans+=solve(i+1,x,y,nums);
                ans=ans%mod;
            }

        }

        dp[i][pre1][pre2]=ans;
        return dp[i][pre1][pre2]%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        long long res  = solve(0,0,50,nums)%mod;

        return res;
    }
};
