class Solution {
public:
    const int mod=1e9+7;
    int dp[2001][51][51];
    int f(int i,int lasta,int lastd,vector<int>& nums)
    {
        if(i==nums.size())
            return 1;
        auto&x=dp[i][lasta][lastd];
        if(x!=-1)
            return x;
        int ans=0;
        for(int val=0;val<=nums[i];val++)
        {
            int upr=val;
            int niche=nums[i]-val;
            if(upr>=lasta&& niche<=lastd)
            {
               ans+=f(i+1,upr,niche,nums);
               ans%=mod;
            }
        }
        return x=ans;
    }
    int countOfPairs(vector<int>& nums) 
    {
        memset(dp,-1,sizeof(dp));
        int ans=f(0,0,50,nums);
        return ans;
    }
};
