class Solution {
public:
    int mod = 1e9 + 7;
    int func(int ind, int prevNum1, vector<int> &nums, vector<vector<int>> &dp){
        if(ind == nums.size()) return 1;
        if(dp[ind][prevNum1] != -1) return dp[ind][prevNum1];
        int prevNum2 = nums[ind-1]-prevNum1;
        long long ans = 0;
        for(int i=prevNum1;i<=nums[ind];i++){
            if(i >= prevNum1 && (nums[ind]-i) <= prevNum2){
                ans = (ans%mod + func(ind+1,i,nums,dp)%mod)%mod;
            }
        }
        return dp[ind][prevNum1] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(51,-1));
        long long ans = 0;
        for(int i=0;i<=nums[0];i++){
            ans = (ans%mod + func(1,i,nums,dp)%mod)%mod;
        }
        return ans;
    }
};
